mapper.select:
SELECT no, type, task, detail, owner, schedule, state, etc, remove FROM TB_TEAM_WORK_REPORT WHERE remove = 'N'
AND task = #{task}
AND type = #{type}
AND owner = #{owner}
AND state = #{state}
AND schedule = #{schedule}

mapper.update:
UPDATE TB_TEAM_WORK_REPORT
SET type = #{type},
    task = #{task},
    detail = #{detail},
    owner = #{owner},
    schedule = #{schedule},
    state = #{state},
    etc = #{etc}

    , delete = #{delete}

WHERE no = #{no}
